import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { useNavigate } from 'react-router-dom';
import jwt_decode from "jwt-decode";


/**
* Navbar
* 
* Creates the navbar for the page. Depending on the users credentials options will be adjusted. For
* example, if the user is an admin, login will be replaced with logout and the admin panel option 
* will be shown.
*
* @author Connor Campbell W18003255
* @collab
*
* @todo - Add functionality for the user to go to their own profile.
        - Add vikings logo somewhere.
*/


export default function ButtonAppBar() {
    let navigate = useNavigate();

    
    /**
    * to[PAGES]
    * 
    * Forwards the user
    */
    

    const toPage = (path) => {
        navigate(path);
    }

    let authenticated = false;
    let admin = false;

    const logout = () => {
        authenticated = false;
        admin = false;
        localStorage.removeItem('UserLoginToken');
        window.location.reload(false);
    }

    if(localStorage.getItem("UserLoginToken")) {
        authenticated = true;
        let decodedToken = jwt_decode(localStorage.getItem("UserLoginToken"))
        if(decodedToken.user_isAdmin == 1) {
            admin = true;
        }
    }

    if(authenticated && admin) {
        console.log("User is admin.")
        return (
        <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static">
            <Box container spacing={2} alignItems="center" sx={{ display: 'flex', flexDirection: 'row', paddingRight: 2 }}>
                <Typography sx={{ fontSize: 24, paddingLeft: 2, paddingRight: 2 }}>
                    NORTHUMBRIA VIKINGS
                </Typography>
                <Button
                    key="toEvents"
                    onClick={() => toPage("events")}
                    sx={{ color: 'white', display: 'block' }}
                >
                    Weekly Events
                </Button>
                <Button
                    key="toResults"
                    onClick={() => toPage("results")}
                    sx={{ color: 'white', display: 'block', height: "100%" }}
                >
                    Results
                </Button>
                <Button
                    key="toTeams"
                    onClick={() => toPage("teams")}
                    sx={{ color: 'white', display: 'block' }}
                >
                    Teams
                </Button>
                <Button
                    key="toAdmin"
                    onClick={() => toPage("admin")}
                    sx={{ color: 'white', display: 'block', marginLeft: "auto" }}
                >
                    Admin
                </Button>
                <Button
                    key="toLogin"
                    onClick={() => toPage("login")}
                    sx={{ color: 'white', display: 'block', }}
                >
                    Logout
                </Button>
            </Box>
        </AppBar>
        </Box>
        )
    }

    if(authenticated && !admin) {
        console.log("User is not admin.")
        return(
            <Box sx={{ flexGrow: 1 }}>
            <AppBar position="static">
                <Box container spacing={2} alignItems="center" sx={{ display: 'flex', flexDirection: 'row', paddingRight: 2 }}>
                    <Typography sx={{ fontSize: 24, paddingLeft: 2, paddingRight: 2 }}>
                        NORTHUMBRIA VIKINGS
                    </Typography>
                    <Button
                        key="toEvents"
                        onClick={() => toPage("events")}
                        sx={{ color: 'white', display: 'block' }}
                    >
                        Weekly Events
                    </Button>
                    <Button
                        key="toResults"
                        onClick={() => toPage("results")}
                        sx={{ color: 'white', display: 'block', height: "100%" }}
                    >
                        Results
                    </Button>
                    <Button
                        key="toTeams"
                        onClick={() => toPage("teams")}
                        sx={{ color: 'white', display: 'block' }}
                    >
                        Teams
                    </Button>
                    <Button
                        key="toLogin"
                        onClick={() => logout()}
                        sx={{ color: 'white', display: 'block', marginLeft: "auto" }}
                    >
                        Logout
                    </Button>
                </Box>
            </AppBar>
        </Box>
        )
    }

    return (
        <Box sx={{ flexGrow: 1 }}>
            <AppBar position="static">
                <Box container spacing={2} alignItems="center" sx={{ display: 'flex', flexDirection: 'row', paddingRight: 2 }}>
                    <Typography sx={{ fontSize: 24, paddingLeft: 2, paddingRight: 2 }}>
                        NORTHUMBRIA VIKINGS
                    </Typography>
                    <Button
                        key="toEvents"
                        onClick={() => toPage("events")}
                        sx={{ color: 'white', display: 'block' }}
                    >
                        Weekly Events
                    </Button>
                    <Button
                        key="toResults"
                        onClick={() => toPage("")}
                        sx={{ color: 'white', display: 'block', height: "100%" }}
                    >
                        Results
                    </Button>
                    <Button
                        key="toTeams"
                        onClick={() => toTeams()}
                        sx={{ color: 'white', display: 'block' }}
                    >
                        Teams
                    </Button>
                    <Button
                        key="toLogin"
                        onClick={() => toLogin()}
                        sx={{ color: 'white', display: 'block', marginLeft: "auto" }}
                    >
                        Login
                    </Button>
                </Box>
            </AppBar>
        </Box>
    );
}
